# Write your code herethe_word = {'python', 'java', 'kotlin', 'javascript'}.pop()print('H A N G M A N')secret_word = '-' * len(the_word)letters = set(the_word)attempts = 8typed_letters = set()menu = ''while menu != 'exit':    menu = input('Type "play" to play the game, "exit" to quit:')    if menu != 'play':        continue    while attempts > 0 and len(letters) > 0:        print('\n')        print(secret_word)        answer = input('Input a letter:').strip()        if len(answer) != 1:            print('You should input a single letter')        elif answer not in 'abcdefghijklmnopqrstuvwxyz':            print('It is not an ASCII lowercase letter')        elif answer in typed_letters:            print('You already typed this letter')        elif answer not in letters:            attempts -= 1            print('No such letter in the word')        else:            letters.discard(answer)            secret_word = the_word            for i in letters:                secret_word = secret_word.replace(i, '-')        typed_letters.add(answer)    if len(letters) == 0:        print(the_word)        print('''You guessed the word!        You survived!''')    else:        print('You are hanged!')